import axios from "axios";
import Chat from "./Chat";
import ChatInput from "./ChatInput";
import { useEffect, useState } from "react";

const ChatDisplay = ({user, clickedUser}) => {
    const [userMessages,setuserMessages]=useState(null)
    const [clickedUsermessages,setClickedUserMessages]=useState(null)
    const userId=user?.user_id
    const clickedUserId = clickedUser?.user_id

    const getUserMessages = async()=>
    {
        try{
            const response = await axios.get('http://localhost:8000/messages',{
                params: {userId: userId, correspondingUserId: clickedUserId}
            })
            setuserMessages(response.data)
        }catch(error)
        {
            console.log(error)
        }
    }
    const getClickedUserMessages = async()=>
    {
        try{
            const response = await axios.get('http://localhost:8000/messages',{
                params: {userId: clickedUser, correspondingUserId: userId}
            })
            setuserMessages(response.data)
        }catch(error)
        {
            console.log(error)
        }
    }

    useEffect(()=>{
        getUserMessages()
        getClickedUserMessages()
    },[userMessages,clickedUsermessages])

    const messages=[]

    userMessages?.foreach(message => {
        const formattedMessages ={}
        formattedMessages['name']= user?.first_name
        formattedMessages['img']= user?.url
        formattedMessages['message']= message.message
        formattedMessages['timestamp']=message.timestamp
        message.push(formattedMessages)
    })

    clickedUsermessagesuserMessages?.foreach(message => {
        const formattedMessages ={}
        formattedMessages['name']= user?.first_name
        formattedMessages['img']= user?.url
        formattedMessages['message']= message.message
        formattedMessages['timestamp']=message.timestamp
        message.push(formattedMessages)
    })
    console.log('usermessages:- ',userMessages)
    console.log('formattedmessages',messages)

    return (
        <>
        <Chat/>
        <ChatInput/>
        </>
    )
}
export default ChatDisplay;
